body {
  opacity: 0.6;
  font-family: Arial, Helvetica, sans-serif;
  width: 90%;
  margin: 0 auto;
  display: flex;
  justify-content: space-around;
}

h1 {
  font-size: x-large;
}
h3 {
  margin: 0;
}

.plants--form {
  display: flex;
  flex-direction: column;
  gap: 6px;
  width: 40%;
}
.plants--form__question {
  font-weight: bold;
}
/* Estilo para los radio buttons y checkboxes personalizados */
.custom-radio input[type="radio"],
.custom-checkbox input[type="checkbox"] {
  display: none; /* Ocultar el input original */
}

.custom-radio label,
.custom-checkbox label {
  position: relative;
  padding-left: 30px; /* Espacio para el indicador personalizado */
  cursor: pointer;
}

.custom-radio label::before,
.custom-checkbox label::before {
  content: "";
  position: absolute;
  top: 2px;
  left: 0;
  width: 15px;
  height: 15px;
  border: 2px solid darkgray; /* Borde morado para el indicador */
  border-radius: 50%; /* Hacerlo circular para los radio buttons */
  box-sizing: border-box;
}

.custom-checkbox label::before {
  border-radius: 5px; /* Hacerlo rectangular para los checkboxes */
}

.custom-radio input[type="radio"]:checked + label::before,
.custom-checkbox input[type="checkbox"]:checked + label::before {
  background-color: #7f03fc; /* Rellenar con morado cuando est√° marcado */
}

.plants--form__buttons-container {
  width: 100%;
  margin: 30px 10px;
}

.button--box {
  color: #7f03fc;
  background-color: white;
  border-color: #7f03fc;
  border-radius: 7px;
  padding: 10px 30px;
}

.button--box:active {
  color: white;
  background-color: #7f03fc;
}

.right--block {
  width: 45%;
}

.info-sheet--container {
  width: 100%;
  height: 600px;
  margin-top: 60px;
  color: white;
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
}

#imageContainer {
  background-color: white;
  position: relative;
  width: 98%;
  height: 400px;
}

#imageContainer img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
.accordion {
  margin-top: 50px;
  display: flex;
  flex-direction: column;
}

.accordion-item {
  border: 1px solid #ccc;
  margin-bottom: 5px;
}

.accordion-header {
  background-color: #f4f4f4;
  padding: 10px;
  cursor: pointer;
}

.accordion-content {
  display: none;
  padding: 10px;
}

.active {
  display: block;
}
